<?php/** * ZZCMS 日志管理 * ============================================================================ * * 版权所有 2014-2016 厦门紫竹数码科技有限公司，并保留所有权利。 * 网站地址: http://www.lnest.com； * ---------------------------------------------------------------------------- * 这不是一个自由软件！您只能在不用于商业目的的前提下对程序代码进行修改和 * 使用；不允许对程序代码以任何形式任何目的的再发布。 */class logs extends Lowxp {	function __construct() {		#按钮		$this->btnMenu = array(			0 => array('url' => '#!logs/index', 'name' => '后台日志'),		);		parent::__construct();		$this->load->model('logs');	}	#管理员日志	function index($page = 1) {		#检索		$conds = $this->getCondsLog();		$condition = " WHERE 1 AND l.sid=".BID;		$condition .= count($conds) ? " AND " . implode(' AND ', $conds) : '';		$orderby = " ORDER BY log_id DESC ";		#非超级管理员帐号，禁止查看超级管理员日志		if (GID > 0) {			$condition .= " AND u.group_id>0";		}		#分页		$this->load->model('page');		$_GET['page'] = intval($page);		$this->page->set_vars(array('per' => (int) $this->common['page_listrows']));		#数据集		$sql = "SELECT l.*,u.username FROM ###_business_user_log AS l " .			"LEFT JOIN ###_business_user AS u ON u.uid=l.user_id ". $condition . $orderby;		$data['list'] = $this->page->hashQuery($sql)->result_array();		$this->smarty->assign('data', $data);		$this->smarty->display('business/logs/list.html');	}	#管理员日志	function search($page = 1) {		#检索		$conds = $this->getCondsLogSearch();		$condition = " WHERE 1 ";		$condition .= count($conds) ? " AND " . implode(' AND ', $conds) : '';		$orderby = " ORDER BY qty DESC ";		#分页		$this->load->model('page');		$_GET['page'] = intval($page);		$this->page->set_vars(array('per' => (int) $this->common['page_listrows']));		#数据集		$sql = "SELECT * FROM ###_search " . $condition . $orderby;		$data['list'] = $this->page->hashQuery($sql)->result_array();		$this->smarty->assign('data', $data);		$this->smarty->assign('btnNo', 5);		$this->smarty->display('manage/logs/list_search.html');	}	/** 获取过滤条件	 * @return array	 */	function getCondsLog() {		$where = array();		#关键词搜索		$array = array('k', 'q');		foreach ($array as $v) {			if (!isset($_GET[$v])) {				$_GET[$v] = '';			}		}		if (!empty($_GET['q'])) {			if ($_GET['k'] == 'username') {				$where[] = "u." . trim($_GET['k']) . " LIKE '%" . addslashes($_GET['q']) . "%'";			} else {				$where[] = "l." . trim($_GET['k']) . " LIKE '%" . addslashes($_GET['q']) . "%'";			}		}		$this->smarty->assign($_GET);		return $where;	}	/** 获取过滤条件	 * @return array	 */	function getCondsLogSearch() {		$where = array();		#关键词搜索		$array = array('k', 'q');		foreach ($array as $v) {			if (!isset($_GET[$v])) {				$_GET[$v] = '';			}		}		if (!empty($_GET['q'])) {			$where[] = "`" . trim($_GET['k']) . "` LIKE '%" . addslashes($_GET['q']) . "%'";		}		$this->smarty->assign($_GET);		return $where;	}	//删除管理员日志    function del_log() {        $log_date = (int) $_POST['log_date'];        if ($log_date < 7) {            $this->tip('请选择您要清除的日期', array('inIframe' => true, 'type' => 1));            die;        }        $time = RUN_TIME - $log_date * 24 * 3600;        $where = "log_time<'$time'";        $this->db->query("DELETE FROM `###_business_user_log` WHERE " . $where);        admin_log_sid('清除管理日志：' . $log_date . '天以前的');        $this->tip('日志清除成功', array('inIframe' => true));        $this->exeJs("parent.location.href='/business#!logs/index';parent.main.refresh();");        die;    }	//删除	function del_search() {		$id = (int) $_POST['id'];		if (!$id) {			die;		}		admin_log_sid('删除搜索词：' . $this->db->getstr("SELECT word FROM ###_search WHERE id=" . $id));		$this->db->delete('###_search', array('id' => $id));		$this->tip('删除成功', array('type' => 1));	}}