<?php/** * ZZCMS 评价管理 * ============================================================================ * * 版权所有 2014-2016 厦门紫竹数码科技有限公司，并保留所有权利。 * 网站地址: http://www.lnest.com； * ---------------------------------------------------------------------------- * 这不是一个自由软件！您只能在不用于商业目的的前提下对程序代码进行修改和 * 使用；不允许对程序代码以任何形式任何目的的再发布。 */class rate extends Lowxp {    function __construct() {        parent::__construct();    }    function index($page = 1) {        #分页        $this->load->model('page');        $_GET['page'] = intval($page);        $this->page->set_vars(array('per' => (int) $this->common['page_listrows']));        $where     = '';        $_GET['q'] = isset($_GET['q']) ? $_GET['q'] : '';        $_GET['k'] = isset($_GET['k']) ? $_GET['k'] : '';        if ($_GET['q']) {            $k = trim($_GET['k']);            $q = trim($_GET['q']);			//如果是会员，就需要查出ID            if($k=="username"){            	$sql                   = "select mid from ###_member where username='".$_GET['q']."'" ;            	$k = "mid";            	$q                = $this->db->getstr($sql);            }            $where .= "AND $k LIKE '%$q%' ";        }        if (isset($_GET['state']) && !empty($_GET['state'])) {            $is_show = intval($_GET['state']) == 99 ? 0 : 1;            $where .= "AND state=$is_show ";        }        $order =  'listorder DESC,id DESC';        #数据集        $sql = "SELECT * FROM ###_goods_rate WHERE mid <> 0 $where ORDER BY $order";        $list = $this->page->hashQuery($sql)->result_array();        foreach ($list as $k => $v) {            $sql                   = "select username from ###_member where mid='{$v['mid']}' ";            $temp_m                = $this->db->get($sql);            $list[$k]['username']  = $temp_m['username'];            $sql                   = "select name from ###_goods where id = '{$v['good_id']}'";            $temp_g                = $this->db->get($sql);            $list[$k]['good_name'] = $temp_g['name'];        }        $this->smarty->assign('list', $list);        $this->smarty->display('manage/order/rate_list.html');    }    //添加评论    function add(){        if (isset($_POST['Submit'])) {            $post = $_POST['post'];            $post = array_map('trim',$post);            if(empty($post['mid'])){                $tmp = $this->get_robots(1);                $post['mid'] = $tmp[0];            }            if($post['star']<=0 || $post['star']>5){                die($this->tip("星数不正确！", array('inIframe' => true)));            }            $is_res = $this->db->get("select 1 from ###_goods where id={$post['good_id']}");            if(!$is_res){                die($this->tip("商品不存在！", array('inIframe' => true)));            }            $post['c_time'] = RUN_TIME;            $res = $this->db->insert("goods_rate",$post);            if($res){                $this->tip("添加成功！", array('inIframe' => true));                $this->exeJs("parent.com.xhide();parent.main.refresh()");            }        }        $this->smarty->display('manage/order/rate_add.html');    }    //删除    function del($id) {        $id = (int) $_POST['id'];        if (!$id) {            die;        }        $this->db->delete('###_goods_rate', array('id' => $id));        $this->tip('删除成功');    }    /**     * 随机获取虚拟数据     */    function get_robots($num = 1){        $num = $num==0?1:abs(intval($num));        $sql = "SELECT mid FROM zz_member WHERE  is_robots=1 and status=1";        $res = $this->db->select($sql);        $res = array_column($res,'mid');        if(count($res)<=$num){            $array = $res;        }else{            $res = array_flip($res);            $array = array_rand($res,$num);        }        if($num==1)$array = array($array);        return $array;    }}