<?php/** * Class invite_model */class invite_model extends Lowxp_Model {	public function getInviteTreeIds($mid, $res = '') {		$sql = "SELECT mid FROM ###_member where ivt_id in( " . $mid . " )";		$tmp = $this->db->select($sql);		if ($tmp) {			$mid = join(',', array_column($tmp, 'mid'));			$res .= ',' . $mid;			$res = $this->getInviteTreeIds($mid, $res);		}		return ltrim($res, ',');	}	public function getInviteLevelList($mid, $level = 0, &$res = array()) {		$sql = "SELECT * FROM ###_member where ivt_id in( " . $mid . " )";		$tmp = $this->db->select($sql);		if ($tmp) {			$res[$level] = $tmp;			$level++;			$mid = join(',', array_column($tmp, 'mid'));			$this->getInviteLevelList($mid, $level, $res);		}		return $res;	}}